import React, {useState} from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useHistory } from 'react-router-dom';
import { useFormik, Form, FormikProvider } from 'formik';
import * as Yup from 'yup';
import { Stack } from '@mui/material';
import { IconButton, InputAdornment } from '@material-ui/core';
import { Icon } from '@iconify/react';

function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright Â© '}
      <Link color="inherit" href="https://codiis.com">
        Connected Digital Systems Pvt Ltd.
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const theme = createTheme();

export default function SignIn() {
  const history = useHistory();
  const [showPassword, setShowPassword] = useState(false);
  const [fail, setFail] = useState();

  const LoginSchema = Yup.object().shape({
    emailIdOrMobileNo: Yup.string().required('Email or Phone is required'),
    password: Yup.string().required('Password is required')
  });

  const formik = useFormik({
    initialValues: {
      emailIdOrMobileNo: '',
      password: '',
    },
    validationSchema: LoginSchema,
    onSubmit: () => {
       postData();
    }
  });

  const postData =async()=>{
    console.log(formik.values)
    let data;
    await axios.post(`${process.env.REACT_APP_API_END_POINT}/dicom/login`,formik.values)
        .then((res)=>data=res.data);
    console.log(data);
    if(data.response){
    window.localStorage.setItem("token",data.response.jwtToken);
    window.localStorage.setItem("fname",data.response.fName)
    window.localStorage.setItem("lname",data.response.lName)
    window.localStorage.setItem("emailId",data.response.emailId)
    }
    if(data.status === 200 ){
      navigate('/dashboard1', { replace: true });
    }else{
      setFail(data.err_msg);
    }
  }

  const { errors, touched, values, isSubmitting,handleSubmit, getFieldProps } = formik;
  const handleShowPassword = () => {
    setShowPassword((show) => !show);
  };

//   const handleSubmit = (event) => {
//     event.preventDefault();
//     const data = new FormData(event.currentTarget);
//     // eslint-disable-next-line no-console
//     console.log({
//       email: data.get('email'),
//       password: data.get('password'),
//     });
//     if(data){
//         history.push('/admin/dashboard');
//     }
//   };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="sm">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 10,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <FormikProvider value={formik}>
         <Form autoComplete="off" noValidate onSubmit={handleSubmit}>
        <Stack spacing={3}>
          <TextField
            fullWidth
            autoComplete="username"
            type="email"
            label="Email or Phone"
            {...getFieldProps('emailIdOrMobileNo')}
            error={Boolean(touched.emailIdOrMobileNo && errors.emailIdOrMobileNo)}
            helperText={touched.emailIdOrMobileNo && errors.emailIdOrMobileNo}
          />

          <TextField
            fullWidth
            autoComplete="current-password"
            type={showPassword ? 'text' : 'password'}
            label="Password"
            {...getFieldProps('password')}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handleShowPassword} edge="end">
                    <Icon icon={showPassword ? '' : ''} />
                  </IconButton>
                </InputAdornment>
              )
            }}
            error={Boolean(touched.password && errors.password)}
            helperText={touched.password && errors.password}
          />
        </Stack>
            <br />
           {fail && <Alert icon={false} severity="error">
              <Typography variant="caption"> {fail}</Typography>
            </Alert>}
      <Stack direction="row" alignItems="center" justifyContent="space-between" sx={{ my: 2 }}>
          <FormControlLabel
            control={<Checkbox checked={values.remember} />}
            label="Remember me"
          />
          <Link variant="subtitle2" href="/forgot-password">
            Forgot password?
          </Link>

          </Stack>

            <br />
        <Button fullWidth size="large" type="submit" variant="contained" color="primary">Login</Button>
        <Stack direction="row" alignItems="center" justifyContent="space-between" sx={{ my: 2 }}>

          <Link href="/signup" variant="body2">
                  {"Don't have an account? Sign Up"}
          </Link>
        </Stack>

      </Form>
    </FormikProvider>
        </Box>
        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
    </ThemeProvider>
  );
}
